void get_connect()
{
    while(1)
    {
        int connect = accept(global_control_param.socketServer, (struct sockaddr*)&global_control_param.addrServer, \
                      &global_control_param.nServerAddrLen);
        global_control_param.client_list.push_back(connect);
    }
}
 
